function customFieldsInit() {
  const fields = document.querySelectorAll('[data-custom-input]')

  if(!fields.length) return
  
  fields.forEach(field => {
    const input = field.querySelector('input')

    input.addEventListener('focus', () => field.classList.add('custom-input--is-active'))

    input.addEventListener('blur', (e) => {
      e.target.value ? '' : field.classList.remove('custom-input--is-active')
    })
  })
}

